$(document).ready(function(){$("span.responsive-menu").click(function(){event.stopPropagation(),$("ul.nav").slideToggle()}),$(document).click(function(){$(window).width()<1025&&"block"===$("span.responsive-menu").css("display")&&$("ul.nav").slideUp()}),$(window).resize(function(){$(window).width()>800&&$("ul.nav").removeAttr("style")})});